INSERT INTO recipes (title, description, ingredients, instructions, calories, protein, carbs, fats, image, author_id, status) VALUES 
('Classic Chicken & Rice', 'The bodybuilder staple. Clean carbs and lean protein.', '200g Chicken Breast\n1 cup Jasmine Rice\n1 cup Broccoli\n1 tbsp Olive Oil', '1. Cook rice.\n2. Grill chicken.\n3. Steam broccoli.\n4. Combine.', 550, 55.0, 60.0, 10.0, 'img/recipes/sample1.jpg', 1, 'approved'),

